var db = require('libs/module_db');
var easyimg = require('easyimage');


exports.cropImage = function(image,x,y,w,h,width,height,kf,callback) {
	var width = kf*width,
		height = kf*height,
		x = kf*x, y = kf*y,
		w = kf*w, h = kf*h;


	easyimg.crop({
              src:image, dst:image,
              cropwidth:w, cropheight:h,
              x:x, y:y,
              gravity: 'NorthWest'
          }).then(
			  function(image) {
			     callback('success');
			  },
			  function (err) {
			    callback('error');
			  }
			);
}

